mapper.select:
SELECT p.product_num as "productNum" ,p.product_title as "productTitle" ,p.product_price as "productPrice" ,p.product_grade as "productGrade" ,p.product_pic as "productPic" ,count(c.member_id) as "wish_count" FROM product p join wish w ON (p.product_num = w.product_num) join wish c on (p.product_num = c.product_num)
WHERE w.member_id = #{value}
GROUP BY p.product_num ORDER BY p.product_num DESC
