mapper.select:

    select
        count(*)
    from
        members
    where
        mem_name = #{memName}
    and
        mem_phone = #{memPhone}
    and
        mem_id = #{memId}

mapper.update:
UPDATE 
    members 
SET 
    mem_pass = #{memPass}
where
    mem_name = #{memName}
and
    mem_phone = #{memPhone}
and
    mem_id = #{memId}
