mapper.insert:
insert into product(
					  product_num
					, product_seller
					, product_cate
					, product_title
					, product_content
					, product_price
					, product_grade
					, product_pic
					, product_readcount
					, product_date 
					, product_status) 
values(#{productNum}
	  ,#{productSeller}
	  ,#{productCate}
	  ,#{productTitle}
	  ,#{productContent}
	  ,#{productPrice}
	  ,#{productGrade}
	  ,#{productPic}
	  ,#{productReadcount}
	  ,#{productDate}
	  ,'판매중'
	  )

mapper.update:
update product set product_title=#{productTitle}, product_content=#{productContent}, product_price=#{productPrice}, product_cate=#{productCate}
					, product_grade=#{productGrade}, product_pic=#{productPic}   
where product_num=#{productNum}

mapper.select:
        select count(*) 
          from product p
          where 1=1
         
                 and p.product_cate = #{productCate}
                
         
                 and p.product_title like '%${searchText}%'
         
         
                  and p.product_price  between #{startPrice} and  #{endPrice}
